import React from "react";

function InfoTooltip({ onClose, status: { isOpen, successful } }) {

    React.useEffect(() => {
      if (!isOpen) return;
      const handleEscClose = (evt) => {
        if (evt.key === 'Escape') {
            onClose()
        };
      };
      document.addEventListener('keyup', handleEscClose);
      return () => {
        document.removeEventListener('keyup', handleEscClose);
      };
    }, [isOpen, onClose])

    return (
      <div className={`popup ${isOpen && 'popup_opened'}`}>
        <form className="popup__container">
          <div className={`popup__status ${successful ? 'popup__status_success' : 'popup__status_error'}`}></div>
          <h2 className="popup__title-status">{successful ? 'Вы успешно зарегистрировались!' : 'Что-то пошло не так! Попробуйте еще раз.'}</h2>
          <button type="button" onClick={onClose} className="popup__close-button"></button>

        </form>
      </div>
    );
  }

  export default InfoTooltip;
