import React, { useState } from "react";
import { Link } from "react-router-dom";

function Register(props) {
  const initialData = {
    email: "",
    password: "",
  };

  const [personData, setPersonData] = useState(initialData);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setPersonData((personData) => ({
      ...personData,
      [name]: value,
    }));
  };

  return (
    <main className="content">
      <form className="singup auth">
        <h1 className="singup__title auth__title">Вход</h1>
        <input
          className="singup__input auth__input"
          type="email"
          name="email"
          id="email-input"
          placeholder="Email"
          required
          minLength="2"
          maxLength="40"
          onChange={handleChange}
          value={personData.email}
        />
        <input
          className="singup__input auth__input"
          type="password"
          name="password"
          id="password-input"
          placeholder="Пароль"
          required
          minLength="2"
          maxLength="40"
          onChange={handleChange}
          value={personData.password}
        />
        <button
          type="submit"
          aria-label="Зарегистрироваться"
          className="register__button auth__button"
        ></button>
         <Link to="/sign-in" className="register__link">
        Уже зарегистрированы? Войти
      </Link>
      </form>
    </main>
  );
}

export default Register;
